import { graphql, gql } from 'react-apollo';

const Viewer = gql`
  query {
    viewer {
      id
      name
      avatar
      shoppingCart {
        totalItemQuantity
      }
    }
  }
`

const withViewer = graphql(Viewer, {
  props: ({data}) => ({
    viewer: data.viewer
  })
})

export default withViewer;
