import React from 'react';
import PropTypes from 'prop-types';

const Divider = ({ background, width }) => {
  const style = {
    height: 1,
    width: width || 23,
    background: background || 'grey'
  }
  return (
    <div style={ style }></div>
  )
}

Divider.propTypes = {
  background: PropTypes.string,
  width: PropTypes.number
}

export default Divider
