import React from 'react';
import PropTypes from 'prop-types';

const MinimalButton = ({ children, style, action }) => {

  const comp_style = {
    border: 'solid .5px',
    borderColor: 'rgba(64, 64, 64, 0.55)',
    backgroundColor: 'transparent',
    color: 'inherit',
    borderRadius: 20,
    padding: '5px 12px',
    fontSize: 14,
    textTransform: 'uppercase',
    display: 'flex',
    alignItems: 'center',
    cursor: 'pointer',
    ...style
  }

  return (
    <button onClick={ action } style={comp_style}>
      { children }
    </button>
  )
}

MinimalButton.propTypes = {
  style: PropTypes.object,
  action: PropTypes.func
}

export default MinimalButton
